import { Fragment, useState, useEffect, useRef } from 'react'
import { Transition } from '@headlessui/react'
import classNames from 'classnames'
import lottie from "lottie-web";
import fireworks from '../../animations/fireworks.json';

type Props = {
  isOpen: boolean
  message: string
  variant?: 'success' | 'warning'
}

export const Alert = ({ isOpen, message, variant = 'warning' }: Props) => {
  const classes = classNames(
    'fixed top-5 left-1/2 transform -translate-x-1/2 max-w-sm w-full shadow-lg rounded-lg pointer-events-auto ring-1 ring-black ring-opacity-5 overflow-hidden',
    {
      'bg-rose-200': variant === 'warning',
      'bg-blue-200 z-20': variant === 'success',
    }
  )

  const Fireworks = () => {
    const anime = useRef<HTMLDivElement>(null);  
    useEffect(() => {
      lottie.loadAnimation({
        container: anime.current as HTMLDivElement,
        renderer: "svg",
        loop: true,
        autoplay: true,
        animationData: fireworks,
      });
      return () => lottie.stop();
    }, []);
    return <div style={{ height: 250, width: 300 }} ref={anime}></div>;
  };

  const [isClicked, setIsClicked] = useState(false);

  if (message == 'Nice job, pal!') {
    return (
      <Transition
        show={isOpen}
        as={Fragment}
        enter="ease-out duration-300 transition"
        enterFrom="opacity-0"
        enterTo="opacity-100"
        leave="transition ease-in duration-100"
        leaveFrom="opacity-100"
        leaveTo="opacity-0"
      >

        <div className={classes}>
        <div>
            <Fireworks />
        </div>
          <div className="p-4">
            <p className="text-sm text-center font-medium text-gray-900">
              {message}
            </p>
          </div>
        </div>
      </Transition>
    )
  } else {
    return (
      <Transition
        show={isOpen}
        as={Fragment}
        enter="ease-out duration-300 transition"
        enterFrom="opacity-0"
        enterTo="opacity-100"
        leave="transition ease-in duration-100"
        leaveFrom="opacity-100"
        leaveTo="opacity-0"
      >
        <div className={classes}>
          <div className="p-4">
            <p className="text-sm text-center font-medium text-gray-900">
              {message}
            </p>
          </div>
        </div>
      </Transition>
    )
  }
}
